mapper.select:
SELECT e.equipmentNum, e.novelNum, e.equipmentName, e.inform, s.statName, es.value, es.statType FROM Equipment e LEFT JOIN EquipmentStat es ON e.equipmentNum = es.equipmentNum LEFT JOIN Stat s ON es.statCode = s.statCode WHERE e.equipmentNum IN
#{id}
ORDER BY e.equipmentNum ASC

SELECT IFNULL((
                  SELECT 1
                  FROM Novel n, Equipment e
                  WHERE e.equipmentNum=#{equipmentNum} AND n.novelNum = e.novelNum AND n.uuid = #{uuid}
                  LIMIT 1
              ), 0)
